INSERT INTO questoes (id, questao, resposta) values (13, '<div>Construa um sistema Web com as seguintes caracterı́sticas:</div><div><b><u><br></u></b></div><div><b><u>Observações:</u></b></div><div><ul><li>O objetivo do sistema é o cadastramento de pessoas e seus dependentes;</li><li>Um dependente é dependente de somente uma pessoa;</li><li>Cada Pessoa possui: id, nome, sobrenome e seus dependentes;</li><li>Cada Dependente possui: id, nome e sobrenome;</li></ul></div><div><br></div><div><b><u>Funcionalidades:</u></b></div><div><ul><li>Listar Pessoas</li><li>Listar Dependentes</li><li>Adicionar uma Pessoa</li><li>Adicionar um Dependente (relacionar o dependente a uma Pessoa)</li><li>Alterar uma Pessoa (os campos devem estar previamente preenchidos);</li><li>Alterar um Dependente (os campos devem estar previamente preenchidos);</li><li>Excluir uma Pessoa;</li><li>Quando excluir uma Pessoa é preciso excluir todos os Dependentes relaciona<span style="background-color: transparent; font-size: 14px;">dos;</span></li><li>Excluir um Dependente;</li></ul></div>',''); 



