INSERT INTO questoes (id, questao, resposta) values (19, '<div>O objetivo é gerenciar horários de atendimento dos professores do IFRS.</div><div><br></div><div><b><u>Funcionalidades</u></b></div><div><ul><li>Deve ser possível&nbsp;agendar novos atendimentos&nbsp;</li><li>Deve ser possível desmarcar atendimento&nbsp;</li><li>Deve ser possível&nbsp;listar todos os atendimentos&nbsp;</li><li>Deverá aparecer na listagem o nome do aluno e as demais colunas</li><li>Deve ser possível&nbsp;cadastrar alunos</li><li>Deve ser possível&nbsp;apagar alunos por matrícula&nbsp;</li><li>Deve ser possível&nbsp;listar todos os alunos</li></ul></div><div><br></div><div><b><u>Exigências</u></b></div><div><ul><li>O atendimento é individual, ou seja, o atendimento é de só um aluno</li><li>Não é possível&nbsp;um mesmo professor realizar atendimentos simultâneos</li><li>Um mesmo aluno poderá agendar muitos atendimentos</li><li>Os alunos possuem matrícula única</li></ul></div>',''); 



