INSERT INTO questoes (id, questao) values (103,'public class Empregado {
	private String nome;
	private boolean habilitado;
	// eh possivel utilizar date ou LocalDate
	private Calendar dataNascimento;

	public Empregado(String nome, Calendar dataNascimento, boolean habilitado){
		this.nome = nome;
		this.habilitado = habilitado;
		this.dataNascimento = dataNascimento;
	}
	// demais getters e setters

	// metodo que retorna a idade (inteiro)
	public int getIdade(){
		// retorna a idade		
		// colocar logica
		return 18;
	}


	public boolean isHabilitado(){
		// true ou false
		return true;
	}
}
// ============================================
public class Carro {
	private String placa;
	// demais atributos de carrro

	// metodo (sem protecao) dentro de carro
	public void manobrar(Empregado empregado){
		System.out.println("Empregado:"+empregado.getNome() + " esta dirigindo o carro:"+this.placa);
	}
}

// ====================================
// proxy
// 1) estender a classe
public class CarroProxy extends Carro {
	// atributo da propria classe
	private Carro carro;

	public CarroProxy(Carro carro){
		this.carro = carro;
	}

	// reescrita do metodo manobrar de carro
	// aqui realizamos a protecao do metodo carro (protection proxy)
	@Override
	public manobrar(Empregado empregado){
		if (empregado.getIdade() >= 18 && empregado.isHabilitado()){
			carro.manobrar(empregado);
		} else {
			throw new Exception("empregado não pode dirigir...");
		}
	}
}'); 

