INSERT INTO questoes (id, questao) values (144,'<h2 style="margin-bottom: 16px; font-weight: 600; font-size: 1.5em; line-height: 1.25; padding-bottom: 0.3em; border-bottom: 1px solid var(--color-border-secondary); color: rgb(36, 41, 46); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" margin-top:="" 0px="" !important;"=""><br></h2><p style="margin-bottom: 16px; color: rgb(36, 41, 46); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" font-size:="" 16px;"="">Construa um gerador de documentos HTML.</p><p style="margin-bottom: 16px; color: rgb(36, 41, 46); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" font-size:="" 16px;"="">Um documento HTML começa com um corpo básico:</p><div class="highlight highlight-text-html-basic position-relative" style="margin-bottom: 16px; color: rgb(36, 41, 46); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" font-size:="" 16px;="" position:="" relative="" !important;"=""><pre style="font-family: ui-monospace, SFMono-Regular, " sf="" mono",="" consolas,="" "liberation="" menlo,="" monospace;="" font-size:="" 13.6px;="" margin-bottom:="" 0px;="" overflow-wrap:="" normal;="" padding:="" 16px;="" line-height:="" 1.45;="" background-color:="" var(--color-bg-tertiary);="" border-radius:="" 6px;="" word-break:="" normal;"=""><span class="pl-kos">&lt;</span><span class="pl-ent" style="color: var(--color-prettylights-syntax-entity-tag);">html</span><span class="pl-kos">&gt;</span>
