html <span class="pl-k" style="color: var(--color-prettylights-syntax-keyword);">=</span> <span class="pl-k" style="color: var(--color-prettylights-syntax-keyword);">new</span> <span class="pl-smi" style="color: var(--color-prettylights-syntax-storage-modifier-import);">Link</span>(html, <span class="pl-s" style="color: var(--color-prettylights-syntax-string);"><span class="pl-pds" style="color: var(--color-prettylights-syntax-string);">"</span>Google<span class="pl-pds" style="color: var(--color-prettylights-syntax-string);">"</span></span>, <span class="pl-s" style="color: var(--color-prettylights-syntax-string);"><span class="pl-pds" style="color: var(--color-prettylights-syntax-string);">"</span>http://www.google.com<span class="pl-pds" style="color: var(--color-prettylights-syntax-string);">"</span></span>);
