INSERT INTO questoes (id, questao) values (154,'<h2 style="margin-bottom: 16px; line-height: 1.25; border-bottom: 1px solid var(--color-border-secondary); padding-bottom: 0.3em;" segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" margin-top:="" 0px="" !important;"=""><p style="margin-bottom: 16px; color: rgb(36, 41, 46); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" font-size:="" 16px;"="">Incorpore ao sistema de venda de ingressos para o cinema o SQLAlchemy respeitando os seguintes relacionamentos já, previamente, definidos nos trabalhos anteriores:</p><p style="margin-bottom: 16px; color: rgb(36, 41, 46); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" font-size:="" 16px;"=""><span style="font-weight: 600;">Lembrando:</span></p><ul style="margin-bottom: 16px; padding-left: 2em; color: rgb(36, 41, 46); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" font-size:="" 16px;"=""><li><p style="margin-bottom: 16px; margin-top: 16px;">1 filme pode ter várias sessões e uma sessão é de um único filme (1 filme =&gt; n sessões)</p></li><li style="margin-top: 0.25em;"><p style="margin-bottom: 16px; margin-top: 16px;">1 ingresso é de uma única sessão mas 1 sessão pode ter vários ingressos (1 sessão =&gt; n ingressos)</p></li><li style="margin-top: 0.25em;"><p style="margin-bottom: 16px; margin-top: 16px;">1 usuário pode comprar vários ingressos (1 usuário =&gt; n ingressos)</p></li></ul><p style="margin-bottom: 16px; color: rgb(36, 41, 46); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" font-size:="" 16px;"=""><span style="font-weight: 600;">Obs:</span>&nbsp;ingresso pode ser visto também como a tabela intermediária entre usuários e sessões. Por outro lado, usuário com ingresso é um relacionamento 1 =&gt; n e sessão para ingressos também é um relacionamento 1 =&gt; n.</p><p style="margin-bottom: 16px; color: rgb(36, 41, 46); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" font-size:="" 16px;"=""><span style="font-weight: 600;">Valor:</span>&nbsp;2</p><p style="margin-bottom: 16px; color: rgb(36, 41, 46); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" font-size:="" 16px;"=""><span style="font-weight: 600;">Prazo:</span>&nbsp;21/07</p></h2>'); 

INSERT INTO questoes (id, questao) values (155,'<h1 style="font-size: 2em; margin-bottom: 16px; font-weight: 600; line-height: 1.25; padding-bottom: 0.3em; border-bottom: 1px solid var(--color-border-muted); color: rgb(36, 41, 47); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" margin-top:="" 0px="" !important;"="">Trabalho 1</h1><h2 style="margin-top: 24px; margin-bottom: 16px; font-size: 1.5em; font-weight: 600; line-height: 1.25; padding-bottom: 0.3em; border-bottom: 1px solid var(--color-border-muted); color: rgb(36, 41, 47); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";"=""><a id="user-content-descrição" class="anchor" href="https://github.com/IgorAvilaPereira/bd2022_1sem/wiki/Trabalhos#descri%C3%A7%C3%A3o" aria-hidden="true" style="color: var(--color-accent-fg); float: left; padding-right: 4px; margin-left: -20px; line-height: 1;"><svg class="octicon octicon-link" viewBox="0 0 16 16" version="1.1" width="16" height="16" aria-hidden="true"><path fill-rule="evenodd" d="M7.775 3.275a.75.75 0 001.06 1.06l1.25-1.25a2 2 0 112.83 2.83l-2.5 2.5a2 2 0 01-2.83 0 .75.75 0 00-1.06 1.06 3.5 3.5 0 004.95 0l2.5-2.5a3.5 3.5 0 00-4.95-4.95l-1.25 1.25zm-4.69 9.64a2 2 0 010-2.83l2.5-2.5a2 2 0 012.83 0 .75.75 0 001.06-1.06 3.5 3.5 0 00-4.95 0l-2.5 2.5a3.5 3.5 0 004.95 4.95l1.25-1.25a.75.75 0 00-1.06-1.06l-1.25 1.25a2 2 0 01-2.83 0z"></path></svg></a>Descrição</h2><p style="margin-bottom: 16px; color: rgb(36, 41, 47); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" font-size:="" 16px;"="">Construa a&nbsp;<span style="font-weight: 600;">Modelagem Conceitual (Modelo ER)</span>, a&nbsp;<span style="font-weight: 600;">Modelagem Lógica (Modelo Relacional)</span>&nbsp;e a&nbsp;<span style="font-weight: 600;">Modelagem Física (SQL)</span>&nbsp;para uma Rede Social semelhante ao Facebook onde:</p><ul style="padding-left: 2em; margin-bottom: 16px; color: rgb(36, 41, 47); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" font-size:="" 16px;"=""><li>Cada Usuário tem: nome, e-mail e senha;</li><li style="margin-top: 0.25em;">Email deve ser único;</li><li style="margin-top: 0.25em;">A senha deve ser armazenada em&nbsp;<em>md5</em></li><li style="margin-top: 0.25em;">Cada Usuário pode criar álbuns de fotos e publicações;</li><li style="margin-top: 0.25em;">Cada álbum tem: título, descrição e fotos;</li><li style="margin-top: 0.25em;">Cada Foto tem: legenda e nome do arquivo;</li><li style="margin-top: 0.25em;">Cada Foto pertence, exclusivamente, a um Álbum;</li><li style="margin-top: 0.25em;">Fotos não podem ser compartilhadas por publicações ou por outros álbuns;</li><li style="margin-top: 0.25em;">Uma Publicação pode ter diversos Arquivos de Mídia;</li><li style="margin-top: 0.25em;">Cada Publicação tem: data e hora de criação, texto e arquivos de mídia;</li><li style="margin-top: 0.25em;">Arquivos de Mídia de uma Publicação não podem ser compartilhados por outras Publicações, ou seja, são exclusivos de uma única Publicação;</li><li style="margin-top: 0.25em;">Um Arquivo de Mídia tem nome e legenda;</li><li style="margin-top: 0.25em;">Um Usuário pode seguir (estabelecer amizade) com outros Usuários;</li><li style="margin-top: 0.25em;">Um Usuário não precisa, necessariamente, seguir de volta os seus seguidores;</li><li style="margin-top: 0.25em;">Cada Usuário pode, em sua&nbsp;<em>timeline</em>, visualizar as publicações dos seus amigos (Usuários que segue/acompanha);</li><li style="margin-top: 0.25em;">É possível também deixar de acompanhar as publicações de um determinado seguidor (sem a necessidade desfazer a amizade);</li><li style="margin-top: 0.25em;">Usuários podem criar Grupos;</li><li style="margin-top: 0.25em;">Cada Grupo tem nome, data de criação, um dono (que deve ser também um Usuário) e usuários participantes;</li><li style="margin-top: 0.25em;"><span style="font-weight: 600;">Importante:</span>&nbsp;O dono do grupo também deve ser um participante do grupo;</li><li style="margin-top: 0.25em;">Dentro de um Grupo é possível criar Publicações (pertinentes somente ao grupo);</li><li style="margin-top: 0.25em;">Todo o Usuário que participa do Grupo pode publicar neste Grupo;</li><li style="margin-top: 0.25em;">É importante armazenar a data e hora do instante em um determinado usuário entrou em um determinado Grupo;</li><li style="margin-top: 0.25em;">Um Grupo pode ter vários usuários e um usuário pode participar de vários Grupos;</li><li style="margin-top: 0.25em;">Publicações de um Grupo são exclusivas do Grupo;</li><li style="margin-top: 0.25em;">Publicações são sempre criadas por Usuários;</li><li style="margin-top: 0.25em;">Publicações de um Grupo devem ser criadas por membros deste Grupo;</li><li style="margin-top: 0.25em;">O Usuário pode ver em sua&nbsp;<em>timeline</em>&nbsp;- além das publicações dos Usuários que segue - as publicações dos Grupos que participa;</li></ul><h2 style="margin-top: 24px; margin-bottom: 16px; font-size: 1.5em; font-weight: 600; line-height: 1.25; padding-bottom: 0.3em; border-bottom: 1px solid var(--color-border-muted); color: rgb(36, 41, 47); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";"=""><a id="user-content-requisitos" class="anchor" href="https://github.com/IgorAvilaPereira/bd2022_1sem/wiki/Trabalhos#requisitos" aria-hidden="true" style="color: var(--color-accent-fg); float: left; padding-right: 4px; margin-left: -20px; line-height: 1;"><svg class="octicon octicon-link" viewBox="0 0 16 16" version="1.1" width="16" height="16" aria-hidden="true"><path fill-rule="evenodd" d="M7.775 3.275a.75.75 0 001.06 1.06l1.25-1.25a2 2 0 112.83 2.83l-2.5 2.5a2 2 0 01-2.83 0 .75.75 0 00-1.06 1.06 3.5 3.5 0 004.95 0l2.5-2.5a3.5 3.5 0 00-4.95-4.95l-1.25 1.25zm-4.69 9.64a2 2 0 010-2.83l2.5-2.5a2 2 0 012.83 0 .75.75 0 001.06-1.06 3.5 3.5 0 00-4.95 0l-2.5 2.5a3.5 3.5 0 004.95 4.95l1.25-1.25a.75.75 0 00-1.06-1.06l-1.25 1.25a2 2 0 01-2.83 0z"></path></svg></a>Requisitos</h2><ol style="padding-left: 2em; margin-bottom: 16px; color: rgb(36, 41, 47); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" font-size:="" 16px;"=""><li><p style="margin-top: 16px; margin-bottom: 16px;">(0.5) Construir a Modelagem Conceitual (ER);</p></li><li style="margin-top: 0.25em;"><p style="margin-top: 16px; margin-bottom: 16px;">(0.5) Construir a Modelagem Lógica (Modelo Relacional);</p></li><li style="margin-top: 0.25em;"><p style="margin-top: 16px; margin-bottom: 16px;">(0.5) Construção a Modelagem Física (SQL - instruções DDL para a criação do B.D);</p></li><li style="margin-top: 0.25em;"><p style="margin-top: 16px; margin-bottom: 16px;">(0.5) Consulta SQL que mostre a&nbsp;<em>timeline</em>&nbsp;de um Usuário que:</p><ul style="padding-left: 2em;"><li><p style="margin-top: 16px; margin-bottom: 16px;">Liste as publicações recentes dos amigos e as publicações dos grupos em que o usuário participa;</p><ul style="padding-left: 2em;"><li><p style="margin-top: 16px; margin-bottom: 16px;">Excluindo publicações de amigos em que o usuário definiu para não acompanhar novas publicações em sua&nbsp;<em>timeline</em>;</p></li><li style="margin-top: 0.25em;"><p style="margin-top: 16px; margin-bottom: 16px;">As publicações devem ser listadas por completo, ou seja, caso exista arquivos de mídia pertecentes a alguma publicação - eles também devem aparecer na consulta;</p></li></ul></li></ul></li></ol><h2 style="margin-top: 24px; margin-bottom: 16px; font-size: 1.5em; font-weight: 600; line-height: 1.25; padding-bottom: 0.3em; border-bottom: 1px solid var(--color-border-muted); color: rgb(36, 41, 47); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";"=""><a id="user-content-data-de-entrega" class="anchor" href="https://github.com/IgorAvilaPereira/bd2022_1sem/wiki/Trabalhos#data-de-entrega" aria-hidden="true" style="color: var(--color-accent-fg); float: left; padding-right: 4px; margin-left: -20px; line-height: 1;"><svg class="octicon octicon-link" viewBox="0 0 16 16" version="1.1" width="16" height="16" aria-hidden="true"><path fill-rule="evenodd" d="M7.775 3.275a.75.75 0 001.06 1.06l1.25-1.25a2 2 0 112.83 2.83l-2.5 2.5a2 2 0 01-2.83 0 .75.75 0 00-1.06 1.06 3.5 3.5 0 004.95 0l2.5-2.5a3.5 3.5 0 00-4.95-4.95l-1.25 1.25zm-4.69 9.64a2 2 0 010-2.83l2.5-2.5a2 2 0 012.83 0 .75.75 0 001.06-1.06 3.5 3.5 0 00-4.95 0l-2.5 2.5a3.5 3.5 0 004.95 4.95l1.25-1.25a.75.75 0 00-1.06-1.06l-1.25 1.25a2 2 0 01-2.83 0z"></path></svg></a>Data de Entrega</h2><ul style="padding-left: 2em; margin-bottom: 16px; color: rgb(36, 41, 47); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" font-size:="" 16px;"=""><li>25/04 pelo AVA</li></ul><h2 style="margin-top: 24px; margin-bottom: 16px; font-size: 1.5em; font-weight: 600; line-height: 1.25; padding-bottom: 0.3em; border-bottom: 1px solid var(--color-border-muted); color: rgb(36, 41, 47); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";"=""><a id="user-content-recomendações" class="anchor" href="https://github.com/IgorAvilaPereira/bd2022_1sem/wiki/Trabalhos#recomenda%C3%A7%C3%B5es" aria-hidden="true" style="color: var(--color-accent-fg); float: left; padding-right: 4px; margin-left: -20px; line-height: 1;"><svg class="octicon octicon-link" viewBox="0 0 16 16" version="1.1" width="16" height="16" aria-hidden="true"><path fill-rule="evenodd" d="M7.775 3.275a.75.75 0 001.06 1.06l1.25-1.25a2 2 0 112.83 2.83l-2.5 2.5a2 2 0 01-2.83 0 .75.75 0 00-1.06 1.06 3.5 3.5 0 004.95 0l2.5-2.5a3.5 3.5 0 00-4.95-4.95l-1.25 1.25zm-4.69 9.64a2 2 0 010-2.83l2.5-2.5a2 2 0 012.83 0 .75.75 0 001.06-1.06 3.5 3.5 0 00-4.95 0l-2.5 2.5a3.5 3.5 0 004.95 4.95l1.25-1.25a.75.75 0 00-1.06-1.06l-1.25 1.25a2 2 0 01-2.83 0z"></path></svg></a>Recomendações</h2><ul style="padding-left: 2em; color: rgb(36, 41, 47); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" font-size:="" 16px;="" margin-bottom:="" 0px="" !important;"=""><li><p style="margin-top: 16px; margin-bottom: 16px;">Colocar todas as cardinalidades do ER;</p></li><li style="margin-top: 0.25em;"><p style="margin-top: 16px; margin-bottom: 16px;">Respeitar regras de mapeamento ER - Modelo Relacional;</p></li><li style="margin-top: 0.25em;"><p style="margin-top: 16px; margin-bottom: 16px;">Colocar tanto no Modelo Relacional quanto no SQL os tipos de cada coluna e suas respectivas restrições de domínio e de integridade:</p><ul style="padding-left: 2em;"><li>Ex: primary key, foreign key, NULL, NOT NULL, DEFAULT, CHECK e etc, serial, integer, text, character, boolean e etc.</li></ul></li></ul>'); 

INSERT INTO questoes (id, questao) values (156,'<h2 style="margin-bottom: 16px; margin-top: 24px; font-weight: 600; font-size: 1.5em; line-height: 1.25; border-bottom: 1px solid var(--color-border-secondary); padding-bottom: 0.3em; color: rgb(36, 41, 46); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";"="">Trabalho 11</h2><p style="margin-bottom: 16px; color: rgb(36, 41, 46); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" font-size:="" 16px;"="">Crie um sistema desktop usando Python, Tkinter e PostgreSQL para o guichê da bilheteria do cinema.</p><p style="margin-bottom: 16px; color: rgb(36, 41, 46); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" font-size:="" 16px;"="">Cada ingresso vendido&nbsp;<em>in loco</em>&nbsp;deve gerar um ticket.</p><p style="margin-bottom: 16px; color: rgb(36, 41, 46); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" font-size:="" 16px;"="">O cliente que chega ao clichê poderá adquirir ingressos pessoalmente somente para as sessões disponíveis.</p><p style="margin-bottom: 16px; color: rgb(36, 41, 46); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" font-size:="" 16px;"=""><span style="font-weight: 600;">Funcionalidades Exigidas:</span></p><ul style="margin-bottom: 16px; padding-left: 2em; color: rgb(36, 41, 46); font-family: -apple-system, BlinkMacSystemFont, " segoe="" ui",="" helvetica,="" arial,="" sans-serif,="" "apple="" color="" emoji",="" "segoe="" ui="" emoji";="" font-